(function () {
'use strict';

window.mf_ready = function () {
  // ページャ部分はvueでのカスタマイズができないためイベントにて差し込む
  var params = this.searchResult.params;
  if (params) {
    // MARS FINDERの仕組みとして最大ページ数を取得する手段は提供されてないらしいので計算する
    var perpage = params.pagemax;
    var current = this.hits > 0 ? params.page : 0;
    var lastpage = Math.ceil(this.hits / perpage);
    document.querySelector('.mf_finder_pager_item_current > span').innerHTML = current + "&nbsp;/&nbsp;" + lastpage + "&nbsp;ページ";

    document.title = (params.q) + "の" + (document.title);
  }

  var prev = document.querySelector('.mf_finder_pager_item_prev .router-link-active span');
  if (prev) { prev.innerHTML = '前へ'; }
  var next = document.querySelector('.mf_finder_pager_item_next .router-link-active span');
  if (next) { next.innerHTML = '次へ'; }
};

}());

